    private void ButonSimpanActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ButonSimpanActionPerformed
        // TODO add your handling code here:
        String uname = user.getText();
        String pass = password.getText();
        String nama = Namapetugas.getText();
        String level = Boxlevel.getSelectedItem().toString();

        try{
            con.createStatement().executeUpdate("insert into petugas value ('"+IdPetugas.getText()+"', '"+uname+"','"+pass+"','"+nama+"','"+level+"')");
            JOptionPane.showMessageDialog(null, "Data petugas berhasil ditambahkan");
            resetForm();
        }catch (Exception ex) {
            System.out.println(""+ex);
            JOptionPane.showMessageDialog(null, "Gagal!");
        }
    }//GEN-LAST:event_ButonSimpanActionPerformed

    private void ButonUpdateActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ButonUpdateActionPerformed
        // TODO add your handling code here:
        try {
            String uname = user.getText();
            String pass = password.getText();
            String nama = Namapetugas.getText();
            String level = Boxlevel.getSelectedItem().toString();

            con.createStatement().executeUpdate("update petugas set username='"+uname+"', password='"+pass+"',nama_petugas='"+nama+"', level='"+level+"' where id_petugas='"+model.getValueAt(Tabel_Petugas.getSelectedRow(),0)+"'");
            JOptionPane.showMessageDialog(this, "Data Berhasil Di Edit");
        }catch (Exception e) {
            System.out.println(e);
        }
        resetForm();
    }//GEN-LAST:event_ButonUpdateActionPerformed

    private void ButonDeleteActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ButonDeleteActionPerformed
        // TODO add your handling code here:
        try {
            con.createStatement().executeUpdate("delete from petugas where id_petugas ='"+model.getValueAt(Tabel_Petugas.getSelectedRow(),0)+"'");
            JOptionPane.showMessageDialog(null, "berhasil");
        }catch (Exception e) {
            JOptionPane.showMessageDialog(null, "gagal");
        }
        resetForm();
    }//GEN-LAST:event_ButonDeleteActionPerformed

