    private void Tabel_PetugasMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_Tabel_PetugasMouseClicked
        // TODO add your handling code here:
        int i = Tabel_Petugas.getSelectedRow();
        IdPetugas.setText(model.getValueAt(i, 0).toString());
        Namapetugas.setText(model.getValueAt(i, 1).toString());
        user.setText(model.getValueAt(i, 2).toString());
        password.setText(model.getValueAt(i, 3).toString());
        Boxlevel.setSelectedItem(model.getValueAt(i, 4));

        //       disabled item on klik row table
        ButonSimpan.setEnabled(false);
        ButonUpdate.setEnabled(true);
        IdPetugas.setEnabled(false);
    }//GEN-LAST:event_Tabel_PetugasMouseClicked

