public void tabelTrans() {
        String[] judul = {"No. Transaksi","ID Petugas","NISN","Tanggal","Bulan Dibayar","Tahun","ID Spp","Jumlah Bayar"};
        model = new DefaultTableModel(judul,0);
        TabelTransaksi.setModel(model);
        String sql = "SELECT * FROM pembayaran WHERE nisn = '"+nisn+"' ";
       
        try {
            rs = con.createStatement().executeQuery(sql);
           
            while(rs.next()) {
               String data[] = {
                rs.getString(1),
                rs.getString(2),
                rs.getString(3),
                rs.getString(4),
                rs.getString(5),
                rs.getString(6),
                rs.getString(7),
                rs.getString(8)};
                model.addRow(data);
           }
        }catch(Exception e) {
           System.out.println(e);
        }
    
    }
